# Import package requests dan BeautifulSoup
import requests
from bs4 import BeautifulSoup

# Request ke website
page = requests.get("https://www.republika.co.id/")

# Extract konten menjadi objek di BeautifulSoup
obj = BeautifulSoup(page.text, 'html.parser');

